import { ADD_TODO, COMPLETE_TODO, SHOW_COMPLETE, SHOW_ALL } from "./actions"
import { combineReducers } from "redux"

const initialState = { todos: [], filter: "ALL" }

const reducer = combineReducers({
	todos: todosReducer,
	filter: filterReducer,
})

export default reducer

function todosReducer(previousState = initialState, action) {
	if (action.type === ADD_TODO) {
		return {
			...previousState,
			todos: [...previousState.todos, { text: action.text, done: false }],
		}
	}

	if (action.type === COMPLETE_TODO) {
		return {
			...previousState,
			todos: previousState.todos.map((todo, index) => {
				if (index === action.index) {
					return { ...todo, done: true }
				}
			}),
		}
	}

	return previousState
}

function filterReducer(previousState = initialState, action) {
	if (action.type === SHOW_ALL) {
		return {
			...previousState,
			filter: "ALL",
		}
	}

	if (action.type === SHOW_COMPLETE) {
		return {
			...previousState,
			filter: "COMPLETE",
		}
	}
	return previousState
}
