import { GET_USERS_SUCCESS } from "../actions"
import { GET_USERS_START } from "../actions"

const initialState = {
	loading: false,
	data: [],
	error: null,
}

export default function users(state = [], action) {
	if (action.type === GET_USERS_START) {
		return {
			...state,
			loading: true,
			error: null,
		}
	}

	if (action.type === GET_USERS_SUCCESS) {
		return {
			...state,
			loading: false,
			error: null,
			data: action.data,
		}
	}

	if (action.type === GET_USERS_FAIL) {
		return {
			...state,
			loading: false,
			error: action.error,
		}
	}

	return state
}
