// 액션의 타입들을 오타를 방지하기 위해 변수로 미리 지정
export const ADD_TODO = "ADD_TODO"

// 액션 생성 함수
function addTodo(todo) {
	return {
		type: ADD_TODO,
		todo,
	}
}
